{"filter":false,"title":"ApprenticeTest.sh","tooltip":"/ApprenticeTest.sh","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":43,"column":0},"action":"insert","lines":["#!/bin/bash","","password_file=\"passwords.txt\"","","mkdir -p ~/.password_manager","","touch ~/.password_manager/$password_file","","save_password() {","    read -p \"サービス名を入力してください：\" service","    read -p \"ユーザー名を入力してください：\" username","    read -s -p \"パスワードを入力してください：\" password","    ","    echo \"$service:$username:$password\" >> ~/.password_manager/$password_file","    echo \"パスワードの追加は成功しました。\"","}","","get_password() {","    read -p \"サービス名を入力してください：\" service","    password=$(grep \"^$service:\" ~/.password_manager/$password_file | cut -d ':' -f 2,3)","    if [ -n \"$password\" ]; then","        echo \"サービス名：$service\"","        echo \"ユーザー名/パスワード：$password\"","    else","        echo \"そのサービスは登録されていません。\"","    fi","}","","main_menu() {","    echo \"パスワードマネージャーへようこそ！\"","    echo \"次の選択肢から入力してください(Add Password/Get Password/Exit)：\"","    read choice","    case $choice in","        \"Add Password\") save_password ;;","        \"Get Password\") get_password ;;","        \"Exit\") echo \"Thank you!\"; exit ;;","        *) echo \"入力が間違えています。Add Password/Get Password/Exit から入力してください。\" ;;","    esac","}","","while true; do","    main_menu","done",""],"id":1}]]},"ace":{"folds":[],"scrolltop":227.99999999999997,"scrollleft":0,"selection":{"start":{"row":29,"column":28},"end":{"row":29,"column":28},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":13,"state":"start","mode":"ace/mode/sh"}},"timestamp":1715845067253,"hash":"d8a2bf7aa0fba2275cbc3acec0463675182d1455"}